function initScreen4% initScreen4 % DOTS_CLUT% 1~100		: left for later uses% 101~255	: luminiance scale% 2004.07.15% Schalllabglobal DOTS_CLUTDOTS_CLUT=zeros(256,3);% from 101~256, gray gradient mapfor i = 101:256,	lvalue = floor(255/(256-101)*(i-101));	DOTS_CLUT(i,:) = lvalue*[1 1 1];endDOTS_CLUT(1,:) = [0 0 0];			% blackDOTS_CLUT(2,:) = [255 255 255];		% whiteDOTS_CLUT(3,:) = [255 0 0];			% Red	DOTS_CLUT(4,:) = [0 255 0];			% GreenDOTS_CLUT(5,:) = [0 0 255];			% Blue