function calibeye_mac(stim_id, stimulus_position_x_pix, stimulus_position_y_pix)% Global variables declared in videoTempoNet.global prepared_stimulus prepared_stim_index% Initialized as zero, so increment before assigning values in prepared_stimulus structure.prepared_stim_index = prepared_stim_index + 1;% Store values for use when presentation command arrives.prepared_stimulus(prepared_stim_index).func_present  = 'calibeye_mac_present';prepared_stimulus(prepared_stim_index).stim_id       = stim_id;prepared_stimulus(prepared_stim_index).position_rect = [(stimulus_position_x_pix - 3) (stimulus_position_y_pix - 3) (stimulus_position_x_pix + 2) (stimulus_position_y_pix + 2)];% Diagnostic.% prepared_stimulus(prepared_stim_index)