function clear_screen_macglobal DEBUGglobal Windowglobal quitKeyif (DEBUG == 0 || DEBUG == 2)    Screen('FillRect',Window,[0 0 0]);    Screen('DrawText',Window,'Looping in videoTempoNet.',100,100,[255 255 255]);    Screen('DrawText',Window,'Waiting for TEMPO protocol to start next visual stimulus.',100,150,[255 255 255]);    Screen('DrawText',Window,['Press [' KbName(quitKey) '] to quit videoTempoNet.'],100,200,[255 255 255]);    Screen('DrawText',Window,'(TEMPO cleared the Mac screen by sending "clear_screen_mac;")',100,250,[255 255 255]);    Screen('Flip',Window);end